enum Color
{
    BLACK,
    BLUE,
    GREEN,
    CYAN,
    RED,
    MAGENTA,
    BROWN,
    LIGHTGRAY,
    DARKGRAY,
    LIGHTBLUE,
    LIGHTGREEN,
    LIGHTCYAN,
    LIGHTRED,
    LIGHTMAGENTA,
    YELLOW,
    WHITE
};

void CursorView(char show) {
    HANDLE hConsole;
    CONSOLE_CURSOR_INFO ConsoleCursor;

    hConsole = GetStdHandle(STD_OUTPUT_HANDLE);

    ConsoleCursor.bVisible = show;
    ConsoleCursor.dwSize = 1;

    SetConsoleCursorInfo(hConsole, &ConsoleCursor);
}

void goToPoint(int x, int y) {
    COORD Cur;
    Cur.X = x;
    Cur.Y = y;

    SetConsoleCursorPosition(GetStdHandle(STD_OUTPUT_HANDLE), Cur);

}


void drw(int bgColor, int textColor) {

    SetConsoleTextAttribute(GetStdHandle(STD_OUTPUT_HANDLE), bgColor * 16 + textColor);

}


void printMap() {
    for (int i = 0; i < mapsero + 1; i++) {
        for (int j = 0; j < mapgaro; j++){
            if (map[i][j] == 1) {
                goToPoint(j, i);
                drw(BROWN, BROWN);
                printf(" ");
                drw(BLACK, BLACK);
            }
            else if(map[i][j] == 0) {
                goToPoint(j, i);
                drw(CYAN, CYAN);
                printf(" ");
                drw(BLACK, BLACK);
            }
            else if(map[i][j] == 2) {
                goToPoint(j, i);
                drw(LIGHTBLUE, LIGHTBLUE);
                printf(" ");
                drw(BLACK, BLACK);
            }

            if(i == mapsero) {
                goToPoint(j, i);
                drw(LIGHTGRAY, LIGHTGRAY);
                printf(" ");
                drw(BLACK, BLACK);
            }
        }
    }
    for(int i = mapsero+1;i < mapsero+6;i++) {
        goToPoint(0, i);
        drw(LIGHTGRAY, LIGHTGRAY);
        printf(" ");
        drw(BLACK, BLACK);

        goToPoint(73, i);
        drw(LIGHTGRAY, LIGHTGRAY);
        printf(" ");
        drw(BLACK, BLACK);

        goToPoint(74, i);
        drw(LIGHTGRAY, LIGHTGRAY);
        printf(" ");
        drw(BLACK, BLACK);

        goToPoint(mapgaro-1, i);
        drw(LIGHTGRAY, LIGHTGRAY);
        printf(" ");
        drw(BLACK, BLACK);
    }
}


void printHP() {
    for (int i = mapsero + 1; i < mapsero + 6; i++) {
        for (int j = 1; j < 73; j++) {
            goToPoint(j, i);
            drw(BLACK, BLACK);
            printf(" ");
        }
    }
    for (int i = mapsero + 1; i < mapsero + 6; i++) {
        for (int j = 1; j <= p1HP; j++) {
            if (j % 9 != 0) {
                goToPoint(j, i);
                drw(RED, RED);
                printf(" ");
                drw(BLACK, BLACK);
            }
            else {
                goToPoint(j, i);
                drw(BLACK, BLACK);
                printf(" ");
            }
        }
    }
}



